import React, { useState, useEffect } from "react";
import axios from "axios";

export default function LightDetect({name, all, lights}) {

	function onToggle(e) {
		lights.forEach((v) => {
			axios.get(`http://${all[v].ip}/toggle`).catch((resp) => {
				console.log("failed to toggle");
			});
		});
	}

	useEffect(() => {
		if (lights.length === 1) {
			axios.get(`http://${all[lights[0]].ip}/status`)
			.then((resp) => {
				if (resp.status) {
					setStatus("active");
				}else {
					setStatus("inactive");
				}
			}).catch(() => {
				setStatus("offline");
			});
		}
	}, [])

	const [Status, setStatus] = useState("loading");

	console.log(name, all, lights);
	return (
    	<div key={name}>
		<p>{name}</p>
		<p>{lights.toString()}</p>
		<input
          		type='checkbox'
			onClick={(e) => onToggle(e)}
			defaultChecked={Status === "active"}
        	/>
		<p>{Status}</p>

    	</div>
  	);
}
