import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { useFormFields } from "../lib/hooks";
import axios from "axios";

function Signup() {

	const URL =  "https://v21dtzztq4.execute-api.us-east-1.amazonaws.com/default/user"

	const [field, handleFieldChange] = useFormFields({
		user: "",
    		password: "",
  	});

	function validateConfirmationForm() {
		return (
      			field.user.length > 0 &&
      			field.password.length > 0
    		);
	}

	function handleSubmit(event) {

		event.preventDefault();


		axios.post(URL, JSON.stringify(field)).then((resp) => {
                	console.log(resp);
		});
	}

	function renderForm() {
		return (
			<Form onSubmit={handleSubmit}>
				<Form.Group controlId="id" size="lg">
					<Form.Label>Username</Form.Label>
					<Form.Control
						autoFocus
						type="text"
						value={field.id}
						onChange={handleFieldChange}
					/>
				</Form.Group>
				<Form.Group controlId="password" size="lg">
					<Form.Label>Password</Form.Label>
					<Form.Control
						type="password"
						value={field.password}
						onChange={handleFieldChange}
					/>
				</Form.Group>
				<Button
					block
					size="lg"
					type="submit"
					variant="success"
				        disabled={!validateConfirmationForm()}

				>
					Signup
				</Button>
			</Form>
		);
	}

	return (
		<div className="Signup">
			{renderForm()}
		</div>
	);
}

export default Signup;
