import logo from './logo.svg';
import './App.css';
import Button from "react-bootstrap/Button";
import React, { useState } from "react";
import LightsDetect from "./components/LightDetect";

function App() {
	const prompts = [
		<p>Plug in Inf-Lights. They should blink <span class="red">red</span></p>,
		<p>Go to wifi settings and connect to <code>P-lights-0</code></p>,
		<p>Searching for lights...</p>
	];

	function incrementPrompt() {
		let nextProm = prom + 1;
		if (nextProm >= prompts.length) {
			nextProm = 0
		}
   		setPrompt(nextProm);
	}

	const [prom, setPrompt] = useState(0);

	return (
    	<div className="App">
      		<header className="App-header">
        		<div>
          			{  prompts[prom] }
        		</div>
				<Button onClick={incrementPrompt}> Next</Button>
      		</header>
    	</div>
  	);
}


export default App;
