import logo from './logo.svg';
import './App.css';
import Button from "react-bootstrap/Button";
import React, { useState } from "react";
import LightsDetect from "./components/LightDetect";

function App() {
	const [prom, setPrompt] = useState(0);

	const prompts = [
		<><p>Plug in Inf-Lights. They should blink <span class="red">red</span></p><Button onClick={() => {updatePrompt()}}> Next</Button>,
		<><p>Go to wifi settings and connect to <code>P-lights-0</code></p><Button onClick={() => {updatePrompt()}}> Next</Button><Button onClick={() => updatePrompt(-1)}> Back</Button></></>,
		<LightsDetect next={updatePrompt}/>,
	];

	function updatePrompt(i = 1) {
		let nextProm = prom + i;
		if (nextProm >= prompts.length) {
			nextProm = 0
		}
   		setPrompt(nextProm);
	}


	return (
    	<div className="App">
      		<header className="App-header">
        		<div>
          			{  prompts[prom] }
        		</div>
      		</header>
    	</div>
  	);
}


export default App;
