import React, { useState, useEffect } from "react";
import axios from "axios";

export default function LightDetect({name, all, lights}) {

	function onToggle(e) {
		lights.forEach((v) => {
			axios.get(`http://${all[v].ip}/toggle`).catch((resp) => {
				console.log("failed to toggle");
			});
		});
	}

	const [Status, setStatus] = useState(undefined);

	console.log(name, all, lights);
	return (
    	<div key={name}>
		<p>{name}</p>
		<p>{lights.toString()}</p>
		<input
          		type='checkbox'
			onClick={(e) => onToggle(e)}
			defaultChecked={Status ? true : false}
        	/>
		{ Status ? <p>Active</p> : <p>Inactive</p> }

    	</div>
  	);
}
