import './Picker2.css';
import React, { useState } from "react";
import { HuePicker, AlphaPicker } from 'react-color'
import logo from '../res/Test.png';
import rgbHex from "rgb-hex";


function Picker2() {

  	const [color, setColor] = useState({hex: "#ff00cb", rgba: {r: 255, g: 0, b: 203, a: 1}, color: {}});

	function onColorChange(hue, c) {
		console.log(c);

		console.log(color);

		let a = c.rgb.a;
		if (!hue) {
			a = color.rgba.a;
		}
		setColor({ hex: "#" + rgbHex(c.rgb.r, c.rgb.g, c.rgb.b, a), rgba: c.rgb})
	}


	function generateBinary(color, pixels) {

		let data = new Uint8Array((2 + pixels) * 8);
		let bytes = getInt64Bytes(pixels);

		let count = (bytes[bytes.length-2] << 8) | bytes[bytes.length-1];

		data[0] = 0x3;
		data[1] = bytes[bytes.length-1];
		data[2] = bytes[bytes.length-2];

		for (let i = 0; i < pixels; i++) {
			let bytes = getInt64Bytes(i);
		 	data[(i + 1) * 8] = 0x1;
			data[(i + 1) * 8 + 1] = color.r;
			data[(i + 1) * 8 + 2] = color.g;
			data[(i + 1) * 8 + 3] = color.b;
			data[(i + 1) * 8 + 4] = bytes[bytes.length-1];
			data[(i + 1) * 8 + 5] = bytes[bytes.length-2];
		}

		data[8 + (pixels * 8)] = 0x2;
		data[8 + (pixels * 8) + 1] = 200;
		data[8 + (pixels * 8) + 2] = 0;
		data[8 + (pixels * 8) + 3] = 0;
		data[8 + (pixels * 8) + 4] = 0;


		console.log(data);
		return data;

	}

	function getInt64Bytes(x) {
  		let y= Math.floor(x/2**32);
  		return [y,(y<<8),(y<<16),(y<<24), x,(x<<8),(x<<16),(x<<24)].map(z=> z>>>24)
	}

	return (
	<div className="Button" style={{background: color.hex}}>
		<img className="ButtonImage" src={logo}></img>
		<HuePicker color={color.hex} onChangeComplete={(c) => onColorChange(false, c)}/>
                <AlphaPicker color={color.hex} onChangeComplete={(c) => onColorChange(true, c)}/>
	</div>
  	);
}


export default Picker2;
