		"Go to wifi settings and connect to <code>P-lights-0</code>",
		"&lt;h1&gt;Hi there!&lt;/h1&gt;",
	];

	function incrementPrompt() {
		let nextProm = prom + 1;
		if (nextProm >= prompts.length) {
			nextProm = 0
		}
   		setPrompt(nextProm);
	}

	const [prom, setPrompt] = useState(0);

