import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Spinner from "react-bootstrap/Spinner";
//import "./Login.css";
import axios from "axios";
const baseURL = "http://192.168.1.115";
const timeout = 3 * 10000;

export default function LightDetect() {
	const [loading, setLoading] = useState(false);
	const [failed, setFailed] = useState(false);
	const [retry] = useState(false);

	useEffect(() => {
  		onLoad();
	}, [retry]);

	async function onLoad() {
		setRetry(false);
		setLoading(true);
		axios.get(baseURL, { timeout: timeout}).then((response) => {
			setLoading(false);
     		console.log(response.data);
    	}).catch((resp) => {
			setLoading(false);
			console.log(resp);
			setFailed(true);
		});
	}

	function clickRetry() {
		setRetry(true);
	}

	return (
    	<div>
			{ loading ? (
				<>
					<p> Searching for lights </p>
					<Spinner animation="border" />
				</>
			) : <></> }
			{ failed ? (
				<>
					<p>Failed to find new lights</p>
					<Button onClick={clickRetry}>Retrty</Button> 
				</>
			) : <></> }
    	</div>
  	);
}
