import '../App.css';
import '../index.css';
import './home.css';
import Login from "./login.js";
import Signup from "./signup.js";
import React, { useState, useEffect } from "react";
import axios from "axios";
import {ToggleButton, ToggleButtonGroup} from "react-bootstrap";

function Home() {


	const [userData, setUserData] = useState({});

	const [authed, setAuthed] = useState(false);

	const [register, setRegister] = useState(false);

	function getUserData() {

		axios.get(`${process.env.REACT_APP_API_URL}/user`).then((resp) => {
			setUserData(resp.data);
		}).catch((e) => {
			setAuthed(false);
			displayError(e);
		});
	}

	function displayError(e) {
		alert(e);
	}

	function renderAuth() {
		return (
			<>
				{ register ?
					<>
					<h3>Login</h3>
					<Login callback={ () => setAuthed(true) }/>
					</>
				:
					<>
					<h3>Signup</h3>
					<Signup callback={ () => setAuthed(true) }/>
					</>

				}
			</>
		)

	}

	useEffect(() => {
		//getUserData()
	}, []);


	return (
		<>
			{ authed ? 
				<p> Logged in </p> 
			:
				<div className="Auth">
				<ToggleButtonGroup type="checkbox" value={register} onChange={(v) => setRegister(!register) }>
      					<ToggleButton id="tbg-btn-1" value={true}>
      						Login	
					</ToggleButton>
      					<ToggleButton id="tbg-btn-3" value={false}>
      						Signup
					</ToggleButton>
    				</ToggleButtonGroup>
					{renderAuth()}
				</div>
			}

		</>
	);
}


export default Home;
