import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Spinner from "react-bootstrap/Spinner";
import axios from "axios";
const baseURL = process.env.REACT_APP_URL + "/networks" ;
const timeout = 3 * 1000;

export default function LightDetect({next}) {
	const [loading, setLoading] = useState(false);
	const [failed, setFailed] = useState(false);
	const [networks, setNetworks] = useState([]);
	const [retry, setRetry] = useState(false);

	useEffect(() => {
  		onLoad();
	}, [retry]);

	async function onLoad() {
		setLoading(true);
		axios.get(baseURL, { timeout: timeout}).then((response) => {
			setLoading(false);
			setNetworks(response.data);
    		}).catch((resp) => {
			setLoading(false);
			console.log(resp);
			setFailed(true);
		});
	}

	function clickRetry() {
		setRetry(!retry);
	}

	return (
    	<div>
			{ loading ? (
				<>
					<p> Searching for lights </p>
					<Spinner animation="border">
					</Spinner>
					<br/>
				</>
			) : failed ? (
				<>
					<p>Failed to find new lights</p>
					<Button onClick={clickRetry}>Retry</Button> 
				</>
			) : (
				<>
					<p>Found new lights!</p>
					<ul>
					{	
