import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { useFormFields } from "../lib/hooks";
import axios from "axios";

function Signup() {

	const URL =  "https://v21dtzztq4.execute-api.us-east-1.amazonaws.com/default/users"

	const [formFeildData, handleFieldChange] = useFormFields({
    		email: "",
		id: "",
    		password: "",
		confirmPassword: "",
    		confirmationCode: "",
  	});

	function validateConfirmationForm() {
		return (
      			formFeildData.email.length > 0 &&
      			formFeildData.password.length > 0 &&
      			formFeildData.password === formFeildData.confirmPassword
    		);
	}

	function handleSubmit() {

		axios.put(URL, JSON.stringify()).then((resp) => {
                        let blob = resp.data;
                        setData(blob);
                });

		console.log("clicked");
	}

	function renderForm() {
		return (
			<Form onSubmit={handleSubmit}>
				<Form.Group controlId="id" size="lg">
					<Form.Label>Username</Form.Label>
					<Form.Control
						autoFocus
						type="text"
						value={formFeildData.id}
						onChange={handleFieldChange}
					/>
				</Form.Group>
				<Form.Group controlId="email" size="lg">
					<Form.Label>Email</Form.Label>
					<Form.Control
						autoFocus
						type="email"
						value={formFeildData.email}
						onChange={handleFieldChange}
					/>
				</Form.Group>
				<Form.Group controlId="password" size="lg">
					<Form.Label>Password</Form.Label>
					<Form.Control
						type="password"
						value={formFeildData.password}
						onChange={handleFieldChange}
					/>
				</Form.Group>
				<Form.Group controlId="confirmPassword" size="lg">
					<Form.Label>Confirm Password</Form.Label>
					<Form.Control
						type="password"
						onChange={handleFieldChange}
						value={formFeildData.confirmPassword}
					/>
				</Form.Group>
				<Button
					block
					size="lg"
					type="submit"
					variant="success"
				        disabled={!validateConfirmationForm()}

				>
					Signup
				</Button>
			</Form>
		);
	}

	return (
		<div className="Signup">
			{renderForm()}
		</div>
	);
}

export default Signup;
