import '../App.css';
import '../index.css';
import 'bootstrap/dist/css/bootstrap.css';
import React, { useState, useEffect } from "react";
import LightListItem from "../components/LightListItem";
import Offcanvas from "react-bootstrap/Offcanvas";
import Button from "react-bootstrap/Button";
import ListGroup from "react-bootstrap/ListGroup";

function Other() {

	async function getData() {

		return {
			lights: [
				{
					id: 0,
					name: "lower tree",
					ip: "192.168.86.144",
					pixels: 100,
				},
				{
					id: 1,
					name: "upper tree",
					ip: "192.168.86.145",
					pixels: 100,
				},
				{
					id: 2,
					name: "test",
					ip: "192.168.86.140",
					pixels: 50,
				}
			],
			groups: [
				{
					name: "lower tree",
					lights: [0],
				},
				{
					name: "upper tree",
					lights: [1],
				},
				{
					name: "test",
					lights: [2],
				},	
				{
					name: "tree",
					lights: [ 0, 1 ],
				}
			]
		}
	}

	useEffect(() => {
		getData().then((resp) => {
			setData(resp);
		    console.log(resp);
		});
	}, []);

	const [data, setData] = useState({});

	const [show, setShow] = useState(false);

	const handleClose = () => setShow(false);
  	const handleShow = () => setShow(true);

 	 return (
		<>
      		<Button variant="primary" onClick={handleShow}>
        		Launch
      		</Button>

      		<Offcanvas show={show} onHide={handleClose} scroll={true} backdrop={false}>
        		<Offcanvas.Header closeButton>
          			<Offcanvas.Title>Lights</Offcanvas.Title>
        		</Offcanvas.Header>
        		<Offcanvas.Body>
	  				<ListGroup>
			 				{
								data.groups ? 
								(data.groups.map((v) => {
									return (
										<ListGroup.Item action>
                                			<LightListItem name={v.name} all={data.lights} lights={v.lights}/>
			 							</ListGroup.Item>
									);
								}))
								: <></>
							}
					</ListGroup>
        		</Offcanvas.Body>
      		</Offcanvas>
    	</>
  	);
}


export default Other;
